import{_ as t,c as i,ae as o,o as s}from"./chunks/framework.D2I48UBN.js";const p=JSON.parse('{"title":"Documentation du Projet PHP8 MVC - Application de Gestion de Photos","description":"","frontmatter":{},"headers":[],"relativePath":"guide/usage.md","filePath":"guide/usage.md"}'),r={name:"guide/usage.md"};function n(a,e,l,u,d,g){return s(),i("div",null,e[0]||(e[0]=[o('<h1 id="documentation-du-projet-php8-mvc-application-de-gestion-de-photos" tabindex="-1">Documentation du Projet PHP8 MVC - Application de Gestion de Photos <a class="header-anchor" href="#documentation-du-projet-php8-mvc-application-de-gestion-de-photos" aria-label="Permalink to &quot;Documentation du Projet PHP8 MVC - Application de Gestion de Photos&quot;">​</a></h1><h2 id="_1-presentation-du-projet" tabindex="-1">1. Présentation du Projet <a class="header-anchor" href="#_1-presentation-du-projet" aria-label="Permalink to &quot;1. Présentation du Projet&quot;">​</a></h2><p>L&#39;application permet aux utilisateurs de partager leurs photos de roadtrips entre amis via un système de groupes privés. Elle propose les fonctionnalités principales suivantes :</p><ul><li><strong>Inscription et authentification</strong> des utilisateurs.</li><li><strong>Création et gestion de groupes de partage</strong>.</li><li><strong>Upload, affichage et suppression de photos</strong>.</li><li><strong>Partage de photos</strong> via des liens publics.</li><li><strong>Réinitialisation de mot de passe</strong> via un email sécurisé.</li><li><strong>Hébergement</strong> accessible via une URL publique en HTTPS.</li></ul><hr><h2 id="_2-architecture-du-projet" tabindex="-1">2. Architecture du Projet <a class="header-anchor" href="#_2-architecture-du-projet" aria-label="Permalink to &quot;2. Architecture du Projet&quot;">​</a></h2><p>Le projet est basé sur l&#39;architecture <strong>MVC (Modèle - Vue - Contrôleur)</strong>, organisée comme suit :</p><ul><li><strong>Models</strong> : Gestion des entités et interactions avec la base de données.</li><li><strong>Controllers</strong> : Traitement des requêtes et actions utilisateur.</li><li><strong>Views</strong> : Présentation des interfaces utilisateur, stylisées avec SASS.</li><li><strong>Core</strong> : Classes utilitaires pour la gestion de la base de données, la session, le routage et l&#39;envoi d&#39;emails.</li><li><strong>Requests</strong> : Validation des entrées utilisateur afin de sécuriser les formulaires.</li></ul><hr><h2 id="_3-fonctionnalites-implementees" tabindex="-1">3. Fonctionnalités Implémentées <a class="header-anchor" href="#_3-fonctionnalites-implementees" aria-label="Permalink to &quot;3. Fonctionnalités Implémentées&quot;">​</a></h2><h3 id="_3-1-authentification-et-gestion-des-utilisateurs" tabindex="-1">3.1 Authentification et Gestion des Utilisateurs <a class="header-anchor" href="#_3-1-authentification-et-gestion-des-utilisateurs" aria-label="Permalink to &quot;3.1 Authentification et Gestion des Utilisateurs&quot;">​</a></h3><ul><li>✅ <strong>Inscription</strong> avec validation stricte et hachage des mots de passe.</li><li>✅ <strong>Connexion sécurisée</strong> utilisant <code>password_verify()</code>.</li><li>✅ <strong>Déconnexion</strong> avec suppression de la session.</li><li>✅ <strong>Réinitialisation de mot de passe</strong> par email via un lien sécurisé.</li></ul><h3 id="_3-2-gestion-des-photos-et-groupes" tabindex="-1">3.2 Gestion des Photos et Groupes <a class="header-anchor" href="#_3-2-gestion-des-photos-et-groupes" aria-label="Permalink to &quot;3.2 Gestion des Photos et Groupes&quot;">​</a></h3><ul><li>✅ <strong>Upload de photos</strong> avec validation (taille et format).</li><li>✅ <strong>Affichage</strong> des photos (par groupe ou personnelles).</li><li>✅ <strong>Création et gestion de groupes</strong> incluant l&#39;ajout/suppression de membres.</li><li>✅ <strong>Gestion des droits d’accès</strong> (lecture/écriture, gestion par le propriétaire du groupe).</li><li>✅ <strong>Suppression de photos</strong> accessible uniquement à l&#39;auteur ou à l&#39;administrateur du groupe.</li><li>✅ <strong>Partage public</strong> de photos via un lien unique généré par l&#39;utilisateur.</li></ul><h3 id="_3-3-gestion-des-emails" tabindex="-1">3.3 Gestion des Emails <a class="header-anchor" href="#_3-3-gestion-des-emails" aria-label="Permalink to &quot;3.3 Gestion des Emails&quot;">​</a></h3><ul><li>✅ <strong>Envoi d&#39;emails</strong> via PHPMailer en utilisant SMTP sécurisé.</li><li>✅ <strong>Réinitialisation de mot de passe</strong> : génération d&#39;un jeton unique et envoi d&#39;un lien sécurisé par email.</li><li>✅ <strong>Configuration SMTP avec Gmail</strong> : <ul><li><strong>Installation</strong> : Exécuter <code>composer require phpmailer/phpmailer</code> à la racine du projet.</li><li><strong>Paramètres</strong> : <ul><li><strong>Host</strong> : <code>smtp.gmail.com</code></li><li><strong>Port</strong> : <code>587</code> (TLS)</li><li><strong>Authentification</strong> : Utilisation d&#39;un mot de passe d&#39;application.</li></ul></li></ul></li><li>✅ <strong>Format HTML</strong> des emails pour une meilleure lisibilité.</li></ul><h3 id="_3-4-logique-des-groupes" tabindex="-1">3.4 Logique des Groupes <a class="header-anchor" href="#_3-4-logique-des-groupes" aria-label="Permalink to &quot;3.4 Logique des Groupes&quot;">​</a></h3><ul><li>✅ <strong>Création d&#39;un groupe</strong> avec un propriétaire unique.</li><li>✅ <strong>Ajout de membres</strong> par l’administrateur du groupe.</li><li>✅ <strong>Attribution de rôles</strong> : <ul><li><strong>read</strong> : Peut voir les photos du groupe.</li><li><strong>write</strong> : Peut ajouter des photos.</li></ul></li><li>✅ <strong>Gestion des groupes</strong> : Le propriétaire peut supprimer le groupe ou modifier les rôles des membres.</li><li>✅ <strong>Partage de photos</strong> : Par défaut limité au groupe, sauf si un lien public est généré par l&#39;utilisateur.</li></ul><h3 id="_3-5-hebergement-et-securisation" tabindex="-1">3.5 Hébergement et Sécurisation <a class="header-anchor" href="#_3-5-hebergement-et-securisation" aria-label="Permalink to &quot;3.5 Hébergement et Sécurisation&quot;">​</a></h3><ul><li>✅ <strong>Déploiement avec Docker</strong> : Utilisation de conteneurs pour PHP, Nginx et MariaDB.</li><li>✅ <strong>Base de données relationnelle</strong> : MySQL avec contraintes d&#39;intégrité.</li><li>✅ <strong>Utilisation de HTTPS</strong> pour sécuriser les communications.</li><li>✅ <strong>Protection</strong> contre les attaques SQL Injection, XSS et CSRF.</li></ul><h4 id="configuration-de-l-hebergement" tabindex="-1">Configuration de l&#39;Hébergement <a class="header-anchor" href="#configuration-de-l-hebergement" aria-label="Permalink to &quot;Configuration de l&#39;Hébergement&quot;">​</a></h4><ul><li><strong>Environnement VPS Docker sur Hostinger</strong> : <ul><li>Le projet a été déployé sur un VPS utilisant Docker.</li><li><strong>Personnalisation du Dockerfile pour Nginx</strong> : <ul><li>Adaptation de la configuration pour le serveur.</li><li>Modification des ports pour correspondre aux besoins du serveur.</li><li>Intégration du certificat SSL dans la configuration.</li></ul></li><li><strong>Mise à jour du fichier <code>.env</code></strong> : <ul><li>Ajout des certificats.</li></ul></li><li><strong>Modification du <code>docker-compose.yml</code></strong> : <ul><li>Réglage des ports pour une communication sécurisée et conforme aux exigences du serveur.</li></ul></li></ul></li></ul>',22)]))}const h=t(r,[["render",n]]);export{p as __pageData,h as default};
